package jan_4;

public class IncrementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 3;
		int result = num++;
		System.out.println(num);
		System.out.println(result);
		
		num = 3;
		result = ++num;
		System.out.println(num);
		System.out.println(result);
	}

}
